import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class DummyApiService {
  private baseUrl = 'https://dummyjson.com'; // Base URL of your API
  private token = '{"id":13,"firstName":"Noah","lastName":"Hernandez","maidenName":"","age":40,"gender":"male","email":"noah.hernandez@x.dummyjson.com","phone":"+49 393-605-6968","username":"noahh","password":"noahhpass","birthDate":"1984-6-5","image":"https://dummyjson.com/icon/noahh/128","bloodGroup":"AB+","height":188.62,"weight":69.49,"eyeColor":"Brown","hair":{"color":"Red","type":"Curly"},"ip":"169.154.126.57","address":{"address":"1413 Maple Street","city":"New York","state":"North Dakota","stateCode":"ND","postalCode":"73696","coordinates":{"lat":-25.0377,"lng":-151.70469},"country":"United States"},"macAddress":"d4:fe:ae:8f:eb:a3","university":"New York University (NYU)","bank":{"cardExpire":"05/26","cardNumber":"1681772579326385","cardType":"Discover","currency":"SEK","iban":"G4UIZKIQVPJM9D31XQVR1E9Z"},"company":{"department":"Engineering","name":"Botsford, Marquardt and Roberts","title":"Database Administrator","address":{"address":"62 Third Street","city":"Seattle","state":"Oregon","stateCode":"OR","postalCode":"83474","coordinates":{"lat":19.490447,"lng":-13.173207},"country":"United States"}},"ein":"877-628","ssn":"660-847-389","userAgent":"Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/95.0.4638.54 Safari/537.36","crypto":{"coin":"Bitcoin","wallet":"0xb9fc2fe63b2a6c003f1c324c3bfa53259162181a","network":"Ethereum (ERC20)"},"role":"moderator"},'; // Replace with your actual token

  constructor(private http: HttpClient) { }

  private getHeaders(): HttpHeaders {
    return new HttpHeaders({
      'Authorization': `Bearer ${this.token}`,
      'Content-Type': 'application/json'
    });
  }

  getData(resource: string, params?: any): Observable<any> {
    let url = `${this.baseUrl}/${resource}`;
    if (params) {
      url += `?${this.serializeParams(params)}`;
    }
    return this.http.get<any>(url, { headers: this.getHeaders() });
  }

  submitForm(endpoint: string, data: any): Observable<any> {
    return this.http.post<any>(`${this.baseUrl}/${endpoint}`, data, { headers: this.getHeaders() });
  }

  updateData(resource: string, id: number, newData: any): Observable<any> {
    return this.http.put<any>(`${this.baseUrl}/${resource}/${id}`, newData, { headers: this.getHeaders() });
  }

  deleteData(resource: string, id: number): Observable<any> {
    return this.http.delete<any>(`${this.baseUrl}/${resource}/${id}`, { headers: this.getHeaders() });
  }

  private serializeParams(params: any): string {
    return Object.keys(params).map(key => `${encodeURIComponent(key)}=${encodeURIComponent(params[key])}`).join('&');
  }
}
