import {Component, HostListener} from "@angular/core";

@Component({
	selector: "navbar",
	templateUrl: "./navbar.component.html",
	styleUrls: ["./navbar.component.css"],
})
export class NavbarComponent {
	showMenu = false;
	isSmallScreen = window.innerWidth <= 992;
	isTransparentBg = true;
	activeSection: string | null = null;

	menuItems = [
		{label: "მთავარი", link: "#home"},
		{label: "ჩვენს შესახებ", link: "#about-us"},
		{label: "მზარეულები", link: "#chefs"},
		{label: "მენიუ", link: "#menu"},
		{label: "გალერეა", link: "#gallery"},
		{label: "კონტაქტი", link: "#contacts"},
	];

	toggleMenu() {
		this.showMenu = !this.showMenu;
	}

	closeMenu() {
		this.showMenu = false;
	}

	@HostListener("window:resize", ["$event"])
	onResize(event: any) {
		this.isSmallScreen = event.target.innerWidth <= 992;
		// Hide the menu when resizing from large to small screen
		if (this.isSmallScreen) {
			this.showMenu = false;
		}
	}

	@HostListener("window:scroll", [])
	onWindowScroll() {
		// Determine the active section based on scrolling
		const sections = ["about-us", "chefs", "menu", "gallery", "contacts"];
		const scrollPosition = window.scrollY;
		let activeSection: string | null = null;

		for (const section of sections) {
			const element = document.getElementById(section);
			if (element) {
				const offsetTop = element.offsetTop;
				if (scrollPosition >= offsetTop - 120) {
				activeSection = section;
				}
			}
		}

		this.activeSection = activeSection;

		// Check if the user has scrolled and remove the transparent background class
		this.isTransparentBg = window.scrollY < 90;
	}

	constructor() {
		// Trigger the initial check for screen size
		this.onResize({target: {innerWidth: window.innerWidth}});
	}
}
<div class="navbar" [class.isTransparentBg]="isTransparentBg">
  <a id="logo" href="#home">
    <img src="https://media.geeksforgeeks.org/gfg-gg-logo.svg" />
  </a>

  <a
    id="openmenu"
    href="#"
    *ngIf="isSmallScreen"
    data-bs-toggle="offcanvas"
    data-bs-target="#offcanvasScrolling"
    aria-controls="offcanvasScrolling"
    (click)="toggleMenu()"
  >
    <i class="fa-solid fa-bars"></i>
  </a>

  <!-- Menu for larger screens -->
  <nav id="nav" [ngStyle]="{ display: !isSmallScreen ? 'flex' : 'none' }">
    <ul class="menu-big p-0">
      <li *ngFor="let item of menuItems">
        <a
          [href]="item.link"
          [class.active]="activeSection === item.link.slice(1)"
        >
          {{ item.label }}
        </a>
      </li>
    </ul>
  </nav>
</div>

<!-- Offcanvas for smaller screens -->
<div
  class="offcanvas offcanvas-start"
  data-bs-scroll="true"
  data-bs-backdrop="false"
  tabindex="-1"
  id="offcanvasScrolling"
  aria-labelledby="offcanvasScrollingLabel"
>
  <div class="offcanvas-header justify-content-end">
    <i
      class="fa-solid fa-xmark"
      data-bs-dismiss="offcanvas"
      aria-label="Close"
      (click)="toggleMenu()"
    ></i>
  </div>
  <div class="offcanvas-body">
    <!-- Menu for the offcanvas -->
    <ul class="menu-small">
      <li *ngFor="let item of menuItems" class="mb-3">
        <a [href]="item.link" (click)="closeMenu()">{{ item.label }}</a>
      </li>
    </ul>
  </div>
</div>
